USE telemedicina;

CREATE TABLE paso_profesionales (
    id_prof INT AUTO_INCREMENT PRIMARY KEY,
    rut CHAR(12) NOT NULL,
    nombres VARCHAR(100) NOT NULL,
    ap_paterno VARCHAR(100) NOT NULL,
    ap_materno VARCHAR(100),
    direccion VARCHAR(100),
    comuna VARCHAR(100),
    ciudad VARCHAR(100),
    email VARCHAR(100),
    telefonos VARCHAR(100),
    especialidad VARCHAR(100),
    registro_rnpi VARCHAR(100),
    fecha_ingreso DATE,
    fecha_egreso DATE,
    zona VARCHAR(50),
    estado VARCHAR(50),
    conara VARCHAR(100)
);



